WKBGeometry {
  Union { 
    WKBPoint point; 
    WKBLineString linestring; 
    WKBPolygon polygon; 
    WKBTriangle triangle 
    WKBPolyhedralSurface polyhedralsurface; 
    WKBTIN tin WKBMultiPoint mpoint; 
    WKBMultiLineString mlinestring; 
    WKBMultiPolygon mpolygon; 
    WKBGeometryCollection 
    collection;
  }
}

